import React, { useEffect, useState, useContext } from 'react';
import { View, Text, FlatList, StyleSheet, ActivityIndicator, TouchableOpacity, Platform, ScrollView } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { AuthContext } from '../context/AuthContext';
import { MaterialIcons } from '@expo/vector-icons';
import moment from 'moment';
import { VictoryPie } from 'victory';

const getBaseUrl = () => Platform.OS === 'android' ? 'http://10.0.2.2:5000' : 'http://localhost:5000';

const COLORS = ["#F39C12", "#E74C3C", "#3498DB", "#2ECC71", "#9B59B6", "#1ABC9C", "#F1C40F", "#E67E22", "#34495E"];

const HistoryScreen = ({ navigation }) => {
  const { user } = useContext(AuthContext);
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchActivities = async () => {
    try {
      const token = await AsyncStorage.getItem('token');
      const res = await fetch(`${getBaseUrl()}/api/activities`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const data = await res.json();
      const finished = data.filter(act => act.startTime && act.endTime);
      setActivities(finished);
    } catch (error) {
      console.error('Error al cargar historial', error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchActivities();
  }, []);

  // Actividades de hoy
  const today = moment().format('YYYY-MM-DD');
  const todayActivities = activities.filter((act) => moment(act.startTime).format('YYYY-MM-DD') === today);

  // Datos para el gr√°fico
  const pieData = todayActivities.map((act, index) => {
    const start = moment(act.startTime);
    const end = moment(act.endTime);
    const duration = moment.duration(end.diff(start)).asMinutes(); // minutos
    return { x: `${index + 1}`, y: duration };
  });

  const renderItem = ({ item, index }) => {
    const start = moment(item.startTime);
    const end = moment(item.endTime);
    const duration = moment.duration(end.diff(start));
    const formattedDuration = [
      String(duration.hours()).padStart(2, '0'),
      String(duration.minutes()).padStart(2, '0'),
      String(duration.seconds()).padStart(2, '0')
    ].join(':');

    return (
      <View style={styles.card}>
        <View style={[styles.colorBox, { backgroundColor: COLORS[index % COLORS.length] }]} />
        <View style={{ flex: 1 }}>
          <Text style={styles.title}>{`${index + 1}. ${item.title}`}</Text>
          <Text>{item.category} / {item.subcategory}</Text>
          <Text style={styles.info}>üü¢ Inicio: {start.format('DD/MM/YYYY HH:mm')}</Text>
          <Text style={styles.info}>üî¥ Fin: {end.format('DD/MM/YYYY HH:mm')}</Text>
          <Text style={styles.duration}>‚è±Ô∏è Duraci√≥n: {formattedDuration}</Text>
        </View>
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <View style={styles.headerRow}>
        <TouchableOpacity onPress={() => navigation.goBack()}>
          <MaterialIcons name="arrow-back" size={28} color="#F39C12" />
        </TouchableOpacity>
        <Text style={styles.header}>üìÑ Historial de Actividades</Text>
      </View>

      {loading ? (
        <ActivityIndicator size="large" color="#3498db" />
      ) : (
        <ScrollView>
          {/* Pie Chart */}
          {pieData.length > 0 && (
            <VictoryPie
              data={pieData}
              colorScale={COLORS}
              innerRadius={50}
              labelRadius={80}
              style={{
                labels: { fill: "#34495E", fontSize: 14, fontWeight: "bold" }
              }}
            />
          )}

          {/* Lista de actividades */}
          <FlatList
            data={todayActivities}
            keyExtractor={(item) => item._id}
            renderItem={renderItem}
            scrollEnabled={false}
            contentContainerStyle={{ paddingBottom: 20 }}
          />
        </ScrollView>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20, backgroundColor: '#f5f6fa' },
  headerRow: { flexDirection: 'row', alignItems: 'center', marginBottom: 20 },
  header: { fontSize: 22, fontWeight: 'bold', marginLeft: 10 },
  card: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    borderRadius: 10,
    marginBottom: 12,
    borderColor: '#ddd',
    borderWidth: 1
  },
  colorBox: {
    width: 20,
    height: 20,
    borderRadius: 4,
    marginRight: 10
  },
  title: { fontSize: 16, fontWeight: 'bold', marginBottom: 4 },
  info: { fontSize: 13, color: '#555' },
  duration: { fontSize: 14, fontWeight: 'bold', marginTop: 4 }
});

export default HistoryScreen;
