import React, { useEffect, useState, useContext } from 'react';
import { View, Text, FlatList, ActivityIndicator, Alert, TouchableOpacity, StyleSheet, Platform } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { AuthContext } from '../context/AuthContext';
import moment from 'moment';

const getBaseUrl = () => {
  return Platform.OS === 'android' ? 'http://10.0.2.2:5000' : 'http://localhost:5000';
};

const HomeScreen = () => {
  const { user } = useContext(AuthContext);
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentActivity, setCurrentActivity] = useState(null);

  const fetchActivities = async () => {
    try {
      const token = await AsyncStorage.getItem('token');
      const response = await fetch(`${getBaseUrl()}/api/activities`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        throw new Error('Error al obtener actividades');
      }

      const data = await response.json();
      setActivities(data);

      // Buscar actividad en curso
      const active = data.find(a => a.startTime && !a.endTime);
      setCurrentActivity(active || null);
    } catch (error) {
      console.error('❌ Error al obtener actividades:', error.message);
      Alert.alert('Error', error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchActivities();
  }, []);

  const handleEndActivity = async () => {
    if (!currentActivity) return;

    try {
      const token = await AsyncStorage.getItem('token');
      const now = new Date();

      const res = await fetch(`${getBaseUrl()}/api/activities/${currentActivity._id}`, {
        method: 'PATCH',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ endTime: now }),
      });

      if (!res.ok) {
        const err = await res.json();
        throw new Error(err.message || 'Error al finalizar actividad');
      }

      Alert.alert("Actividad finalizada", `Terminada a las ${now.toLocaleTimeString()}`);
      fetchActivities();
    } catch (error) {
      console.error('❌ Error al finalizar actividad:', error.message);
      Alert.alert('Error', error.message);
    }
  };

  const renderItem = ({ item }) => {
    const isActive = item._id === currentActivity?._id;
    const duration = isActive
      ? moment.duration(moment().diff(moment(item.startTime))).humanize()
      : item.startTime && item.endTime
      ? moment.duration(moment(item.endTime).diff(moment(item.startTime))).humanize()
      : null;

    return (
      <TouchableOpacity
        style={[
          styles.item,
          isActive && { backgroundColor: '#f1c40f' }
        ]}
      >
        <Text style={styles.title}>{item.title}</Text>
        <Text>{item.category} / {item.subcategory}</Text>
        <Text style={styles.time}>
          {item.startTime ? `Inicio: ${new Date(item.startTime).toLocaleTimeString()}` : ''}
        </Text>
        <Text style={styles.time}>
          {item.endTime ? `Fin: ${new Date(item.endTime).toLocaleTimeString()}` : ''}
        </Text>
        {duration && (
          <Text style={[styles.duration, isActive && styles.liveDuration]}>
            ⏱ Tiempo: {duration}
          </Text>
        )}
      </TouchableOpacity>
    );
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Bienvenido, {user?.firstName} ({user?.role})</Text>

      {currentActivity && (
        <View style={styles.activeContainer}>
          <Text style={styles.activeText}>Actividad en curso: <Text style={{ fontWeight: 'bold' }}>{currentActivity.title}</Text></Text>
          <TouchableOpacity style={styles.endButton} onPress={handleEndActivity}>
            <Text style={styles.endButtonText}>⏹ Finalizar actividad</Text>
          </TouchableOpacity>
        </View>
      )}

      {loading ? (
        <ActivityIndicator size="large" color="#3498db" />
      ) : (
        <FlatList
          data={activities}
          keyExtractor={(item) => item._id}
          renderItem={renderItem}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: { padding: 20, flex: 1 },
  header: { fontSize: 20, fontWeight: 'bold', marginBottom: 15 },
  item: {
    padding: 15,
    marginBottom: 10,
    backgroundColor: '#ecf0f1',
    borderRadius: 10,
  },
  title: { fontSize: 16, fontWeight: 'bold' },
  time: { fontSize: 12, color: '#2c3e50' },
  duration: { marginTop: 5, fontSize: 13, color: '#555' },
  liveDuration: { fontWeight: 'bold', color: '#e67e22' },
  activeContainer: {
    backgroundColor: '#dff9fb',
    padding: 15,
    marginBottom: 20,
    borderRadius: 10,
  },
  activeText: { fontSize: 16, marginBottom: 10 },
  endButton: {
    backgroundColor: '#e74c3c',
    padding: 10,
    borderRadius: 8,
    alignItems: 'center'
  },
  endButtonText: {
    color: '#fff',
    fontWeight: 'bold'
  }
});

export default HomeScreen;
